import{_ as r,o as t,c as s,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-808 Licenses required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-808.md","filePath":"vacode/29.1-808.md"}'),n={name:"vacode/29.1-808.md"},o=e("h1",{id:"§-29-1-808-licenses-required",tabindex:"-1"},[a("§ 29.1-808 Licenses required. "),e("a",{class:"header-anchor",href:"#§-29-1-808-licenses-required","aria-label":'Permalink to "§ 29.1-808 Licenses required."'},"​")],-1),i=e("p",null,"It is unlawful for any person to engage in business in this Commonwealth as a new watercraft dealer, used watercraft dealer, watercraft salesman, watercraft demonstrator, manufacturer, factory branch, distributor, distributor branch, or factory or distributor representative without first obtaining a license as provided in this chapter. If any watercraft dealer acts as a watercraft salesman, he shall obtain a watercraft salesman's license in addition to a watercraft dealer's license. Any watercraft salesman who acts as a watercraft demonstrator shall not be required to obtain an additional license as a watercraft demonstrator. The offering or granting of a watercraft dealer franchise in this Commonwealth shall be deemed the engaging in business in this Commonwealth for purposes of this section, and no new watercraft may be sold or offered for sale in this Commonwealth unless the franchisor of watercraft dealer franchises for that line-make in this Commonwealth (whether such franchisor be a manufacturer, factory branch, distributor, distributor branch, or otherwise) is licensed under this chapter. In the event a license issued under this chapter to a franchisor of watercraft dealer franchises shall be suspended or revoked or shall not be renewed, nothing in this section shall be deemed to prevent the sale of any new watercraft of such franchisor's line-make, manufactured in or brought into this Commonwealth for sale prior to the suspension, revocation or expiration of the license.",-1),c=e("p",null,[a("1988, c. 592; 1998, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0515",target:"_blank",rel:"noreferrer"},"515"),a(".")],-1),l=[o,i,c];function h(d,f,u,m,w,p){return t(),s("div",null,l)}const g=r(n,[["render",h]]);export{_ as __pageData,g as default};
