import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 29.1-819 Grounds for denying, suspending or revoking licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-819.md","filePath":"vacode/29.1-819.md"}'),o={name:"vacode/29.1-819.md"},a=n('<h1 id="§-29-1-819-grounds-for-denying-suspending-or-revoking-licenses" tabindex="-1">§ 29.1-819 Grounds for denying, suspending or revoking licenses. <a class="header-anchor" href="#§-29-1-819-grounds-for-denying-suspending-or-revoking-licenses" aria-label="Permalink to &quot;§ 29.1-819 Grounds for denying, suspending or revoking licenses.&quot;">​</a></h1><p>A license may be denied, suspended or revoked on any one or more of the following grounds:</p><ol><li><p>Material misstatement in application for license.</p></li><li><p>Willful failure to comply with any provision of this chapter or any lawful regulation promulgated by the Board under this chapter.</p></li><li><p>Being a watercraft dealer, failure to have an established place of business as defined in § <a href="http://law.lis.virginia.gov/vacode/29.1-801/" target="_blank" rel="noreferrer">29.1-801</a>.</p></li><li><p>Willfully defrauding any retail buyer, to the buyer&#39;s damage, or any other person in the conduct of the licensee&#39;s business.</p></li><li><p>Employment of fraudulent devices, methods or practices in connection with compliance with the requirements under the statutes of this Commonwealth with respect to the retaking of watercraft under retail installment contracts and the redemption and resale of such watercraft.</p></li><li><p>Having used unfair methods of competition or unfair deceptive acts or practices.</p></li><li><p>Knowingly advertising by any means any assertion, representation or statement of fact which is untrue, misleading or deceptive in any particular relating to the conduct of the business licensed or for which a license is sought.</p></li><li><p>Having been convicted of any fraudulent act in connection with the business of selling watercraft.</p></li><li><p>Having been convicted of a crime involving the acquisition or transference of title to a watercraft.</p></li><li><p>Willfully retaining title to a watercraft that has not been completely and legally assigned.</p></li><li><p>Failure to submit to the Director any application or fees collected for the Department on behalf of the buyer within thirty days of receipt.</p></li></ol><p>1988, c. 592.</p>',4),r=[a];function s(l,c,d,p,f,u){return t(),i("div",null,r)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
