import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-105 Century farm program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-105.md","filePath":"vacode/3.2-105.md"}'),o={name:"vacode/3.2-105.md"},n=t('<h1 id="§-3-2-105-century-farm-program" tabindex="-1">§ 3.2-105 Century farm program. <a class="header-anchor" href="#§-3-2-105-century-farm-program" aria-label="Permalink to &quot;§ 3.2-105 Century farm program.&quot;">​</a></h1><p>The Commissioner shall establish a century farm program to honor farm families in the Commonwealth whose property has been in the same family for 100 years or more. In order to be eligible for recognition under the program, a farm shall (i) have been owned by the same family for at least 100 consecutive years; (ii) be lived on, or actually farmed, by a descendant of the original owners; and (iii) gross more than $2,500 annually from the sale of farm products.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0161" target="_blank" rel="noreferrer">161</a>, § 3.1-17.1; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0006" target="_blank" rel="noreferrer">6</a>.</p>',3),i=[n];function l(s,c,f,m,h,p){return r(),a("div",null,i)}const d=e(o,[["render",l]]);export{g as __pageData,d as default};
