import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-1201 Apple Board; composition and appointment of members.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1201.md","filePath":"vacode/3.2-1201.md"}'),i={name:"vacode/3.2-1201.md"},r=a('<h1 id="§-3-2-1201-apple-board-composition-and-appointment-of-members" tabindex="-1">§ 3.2-1201 Apple Board; composition and appointment of members. <a class="header-anchor" href="#§-3-2-1201-apple-board-composition-and-appointment-of-members" aria-label="Permalink to &quot;§ 3.2-1201 Apple Board; composition and appointment of members.&quot;">​</a></h1><p>A. The Apple Board is continued within the Department. The Apple Board shall consist of six members, with two members representing each district. Each member shall be a citizen of the Commonwealth and engaged in producing apples in the Commonwealth with a majority of his apple production occurring in the district he represents.</p><p>B. The Commissioner shall hold a special election in each district to elect each member of the Apple Board. The special election shall be held by secret ballot at least 30 days but not more than 90 days before the expiration of the term of office of any member. The Commissioner shall appoint the candidate receiving the highest number of votes in the special election as a member. The Apple Board may adopt and enforce regulations governing the conduct of special elections and voting therein. Such regulations shall be exempt from Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4006/" target="_blank" rel="noreferrer">2.2-4006</a> et seq.) of the Administrative Process Act. A producer shall be eligible to vote only in the district where the majority of his apple production occurs.</p><p>Code 1950, § 3-514; 1958, c. 148; 1964, c. 578; 1966, c. 702, § 3.1-634; 1974, c. 601; 1985, c. 448; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>, § 3.1-634.1; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0214" target="_blank" rel="noreferrer">214</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0565" target="_blank" rel="noreferrer">565</a>.</p>',4),n=[r];function l(s,c,p,h,m,d){return t(),o("div",null,n)}const b=e(i,[["render",l]]);export{g as __pageData,b as default};
