import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 3.2-1212 Referenda results; action of Governor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1212.md","filePath":"vacode/3.2-1212.md"}'),t={name:"vacode/3.2-1212.md"},n=o('<h1 id="§-3-2-1212-referenda-results-action-of-governor" tabindex="-1">§ 3.2-1212 Referenda results; action of Governor. <a class="header-anchor" href="#§-3-2-1212-referenda-results-action-of-governor" aria-label="Permalink to &quot;§ 3.2-1212 Referenda results; action of Governor.&quot;">​</a></h1><p>If the Governor finds the referendum in order and that more than one-half of those voting are in favor of the excise tax on apples pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-1210/" target="_blank" rel="noreferrer">3.2-1210</a>, then the Governor shall so proclaim. Upon such proclamation by the Governor, the excise tax on apples shall be established. If the Governor finds that more than one-half of those voting are in opposition to the excise tax on apples pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-1210/" target="_blank" rel="noreferrer">3.2-1210</a>, then the Governor shall not so proclaim and the excise tax on apples shall not be established.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0875" target="_blank" rel="noreferrer">875</a>, § 3.1-636.6; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',3),i=[n];function s(l,h,c,f,p,d){return r(),a("div",null,i)}const g=e(t,[["render",s]]);export{v as __pageData,g as default};
