import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-1213 Referenda.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1213.md","filePath":"vacode/3.2-1213.md"}'),n={name:"vacode/3.2-1213.md"},o=t('<h1 id="§-3-2-1213-referenda" tabindex="-1">§ 3.2-1213 Referenda. <a class="header-anchor" href="#§-3-2-1213-referenda" aria-label="Permalink to &quot;§ 3.2-1213 Referenda.&quot;">​</a></h1><p>The Board, upon petition by at least 10 percent of the producers in the Commonwealth as determined by the Commissioner, shall provide for a referendum on the continuation of the Apple Board or amending the excise tax on apples if established pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-1208/" target="_blank" rel="noreferrer">3.2-1208</a>. The Board shall not act on such a petition for conducting a referendum until at least five years have passed since the last referendum. Any referendum held under this section shall be conducted in accordance with this chapter.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0875" target="_blank" rel="noreferrer">875</a>, § 3.1-636.7; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',3),i=[o];function s(c,l,d,h,f,p){return a(),r("div",null,i)}const g=e(n,[["render",s]]);export{u as __pageData,g as default};
