import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-1304 Powers and duties of Cattle Industry Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1304.md","filePath":"vacode/3.2-1304.md"}'),r={name:"vacode/3.2-1304.md"},n=o('<h1 id="§-3-2-1304-powers-and-duties-of-cattle-industry-board" tabindex="-1">§ 3.2-1304 Powers and duties of Cattle Industry Board. <a class="header-anchor" href="#§-3-2-1304-powers-and-duties-of-cattle-industry-board" aria-label="Permalink to &quot;§ 3.2-1304 Powers and duties of Cattle Industry Board.&quot;">​</a></h1><p>A. The Cattle Industry Board shall be responsible for the promotion and economic development of the Virginia cattle industry and of beef products, including the improvement of the commercial value of cattle for Virginia producers.</p><p>B. The Board may expend funds collected pursuant to § <a href="/vacode/3.2-1306/">3.2-1306</a> to provide for programs to serve the Virginia cattle industry for market development, education, publicity, research, and the promotion of the sale and use of cattle and beef products; to manage the funds so as to accumulate a reserve for contingencies; to establish an office and employ such technical, professional, and other assistants as may be required; and to contract for market development, publicity, research, advertising, and other promotional services.</p><p>C. The Board shall establish a meeting place anywhere within the Commonwealth, but the selection of the location shall be guided by consideration for the convenience of the majority of those most likely to have business with the Board or to be affected by this chapter.</p><p>D. An annual report shall be made by the Board to the Commissioner and shall be published as a public record to include a statement on receipts and itemized disbursements of the Virginia Cattle Industry Fund.</p><p>1970, c. 310, §§ 3.1-796.14, 3.1-796.22 to 3.1-796.24; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0158" target="_blank" rel="noreferrer">158</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0565" target="_blank" rel="noreferrer">565</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0469" target="_blank" rel="noreferrer">469</a>.</p>',6),i=[n];function s(d,l,c,h,p,f){return t(),a("div",null,i)}const b=e(r,[["render",s]]);export{m as __pageData,b as default};
