import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-1305 Virginia Cattle Industry Fund established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1305.md","filePath":"vacode/3.2-1305.md"}'),n={name:"vacode/3.2-1305.md"},i=r('<h1 id="§-3-2-1305-virginia-cattle-industry-fund-established" tabindex="-1">§ 3.2-1305 Virginia Cattle Industry Fund established. <a class="header-anchor" href="#§-3-2-1305-virginia-cattle-industry-fund-established" aria-label="Permalink to &quot;§ 3.2-1305 Virginia Cattle Industry Fund established.&quot;">​</a></h1><p>There is hereby created in the state treasury a special nonreverting fund to be known as the Virginia Cattle Industry Fund, hereinafter referred to as &quot;the Fund.&quot; The Fund shall be established on the books of the Comptroller. All funds collected pursuant to § <a href="/vacode/3.2-1306/">3.2-1306</a> shall be paid into the state treasury and credited to the Fund. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund.</p><p>All moneys credited to the Fund shall be used exclusively as set forth in this chapter. The Auditor of Public Accounts shall audit all the accounts of the Board as is provided for in § <a href="/vacode/30-133/">30-133</a>. Expenditures and disbursements from the Fund shall be made by the Board on warrants issued by the Comptroller upon written request signed by a duly authorized officer of the Board.</p><p>1970, c. 310, § 3.1-796.26; 1978, c. 540; 1983, c. 375; 1985, c. 237; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0158" target="_blank" rel="noreferrer">158</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0469" target="_blank" rel="noreferrer">469</a>.</p>',4),s=[i];function o(d,l,h,c,u,f){return t(),a("div",null,s)}const b=e(n,[["render",o]]);export{_ as __pageData,b as default};
