import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-1505 Referenda.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1505.md","filePath":"vacode/3.2-1505.md"}'),n={name:"vacode/3.2-1505.md"},o=t('<h1 id="§-3-2-1505-referenda" tabindex="-1">§ 3.2-1505 Referenda. <a class="header-anchor" href="#§-3-2-1505-referenda" aria-label="Permalink to &quot;§ 3.2-1505 Referenda.&quot;">​</a></h1><p>The Board, upon petition by at least 10 percent of the producers in the Commonwealth as determined by the Commissioner, shall provide for either a referendum on the continuation of the Cotton Board and the assessment on cotton by a maximum of $0.15 per bale. Any referendum held under this section shall be conducted in accordance with this chapter. The Board shall adopt regulations governing the conduct of referenda pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-112/" target="_blank" rel="noreferrer">3.2-112</a>.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0873" target="_blank" rel="noreferrer">873</a>, § 3.1-1089; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0326" target="_blank" rel="noreferrer">326</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',3),i=[o];function c(s,d,l,h,f,_){return a(),r("div",null,i)}const u=e(n,[["render",c]]);export{g as __pageData,u as default};
