import{_ as e,o as t,c as o,O as s}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-1512 Collection and disposition of assessment by handler; reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1512.md","filePath":"vacode/3.2-1512.md"}'),a={name:"vacode/3.2-1512.md"},r=s('<h1 id="§-3-2-1512-collection-and-disposition-of-assessment-by-handler-reports" tabindex="-1">§ 3.2-1512 Collection and disposition of assessment by handler; reports. <a class="header-anchor" href="#§-3-2-1512-collection-and-disposition-of-assessment-by-handler-reports" aria-label="Permalink to &quot;§ 3.2-1512 Collection and disposition of assessment by handler; reports.&quot;">​</a></h1><p>A. Every handler shall collect an assessment of 95 cents ($0.95) per bale from the owner of all cotton that the handler gins for any owner and shall remit such assessment to the Tax Commissioner on or before the last day of the month following the end of each calendar quarter. Such assessment shall be in addition to any moneys collected by the handler as authorized by the Cotton Research and Promotion Act (7 U.S.C. §§ 2101 -2118). The Tax Commissioner shall promptly pay the assessments into the state treasury to the credit of the Virginia Cotton Fund.</p><p>B. Every handler shall complete reports on forms furnished by the Tax Commissioner, submit such reports to the Tax Commissioner along with the assessments submitted pursuant to subsection A, and keep copies of the reports for a period of not less than three years from the time the report was produced. Each report shall consist of information for the calendar quarter preceding the month such report is due and shall include the following: (i) the number of bales that the handler has ginned; (ii) the dollar amount of assessments collected by the handler; (iii) a list of those from whom an assessment has been collected for cotton ginned by the handler; (iv) the dollar amounts of all assessments collected from each owner of cotton ginned by the handler; and (v) any other information deemed necessary by the Tax Commissioner to carry out his duties under this chapter. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/58.1-3/" target="_blank" rel="noreferrer">58.1-3</a>, upon request, the Tax Commissioner shall provide to the Cotton Board or the Commissioner copies of reports submitted pursuant to this section.</p><p>C. Any assessment that is not paid when due shall be collected pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-1102/" target="_blank" rel="noreferrer">3.2-1102</a>.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0873" target="_blank" rel="noreferrer">873</a>, § 3.1-1098; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0565" target="_blank" rel="noreferrer">565</a>.</p>',5),n=[r];function i(l,h,d,c,p,m){return t(),o("div",null,n)}const g=e(a,[["render",i]]);export{u as __pageData,g as default};
