import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 3.2-1513 Records to be kept by handler.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1513.md","filePath":"vacode/3.2-1513.md"}'),o={name:"vacode/3.2-1513.md"},n=r('<h1 id="§-3-2-1513-records-to-be-kept-by-handler" tabindex="-1">§ 3.2-1513 Records to be kept by handler. <a class="header-anchor" href="#§-3-2-1513-records-to-be-kept-by-handler" aria-label="Permalink to &quot;§ 3.2-1513 Records to be kept by handler.&quot;">​</a></h1><p>Every handler shall maintain the following records for all cotton ginned by the handler for the owner of the cotton:</p><ol><li><p>Full name and address of the owner of the cotton;</p></li><li><p>Date the cotton was ginned by the handler for such owner;</p></li><li><p>Number of bales ginned; and</p></li><li><p>Dollar amount of assessment collected by the handler from the owner.</p></li></ol><p>The handler shall maintain such records for a period of not less than three years from the time the cotton was ginned. Such records shall be open to the inspection of the Tax Commissioner and shall be established and maintained as required by the Tax Commissioner.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0873" target="_blank" rel="noreferrer">873</a>, § 3.1-1099; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),l=[n];function i(s,h,d,c,p,f){return t(),a("div",null,l)}const m=e(o,[["render",i]]);export{b as __pageData,m as default};
