import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse(`{"title":"§ 3.2-1705 Management of referenda; Commissioner's duties; notice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1705.md","filePath":"vacode/3.2-1705.md"}`),n={name:"vacode/3.2-1705.md"},o=t('<h1 id="§-3-2-1705-management-of-referenda-commissioner-s-duties-notice" tabindex="-1">§ 3.2-1705 Management of referenda; Commissioner&#39;s duties; notice. <a class="header-anchor" href="#§-3-2-1705-management-of-referenda-commissioner-s-duties-notice" aria-label="Permalink to &quot;§ 3.2-1705 Management of referenda; Commissioner&#39;s duties; notice.&quot;">​</a></h1><p>A. The Commissioner shall arrange for and manage any referendum conducted under this chapter.</p><p>B. The Commissioner shall, at least 60 days before the date upon which a referendum is to be held, mail notice to the clerk of the circuit court in each locality where those eligible to vote in the referendum reside. The clerk of the circuit court shall post the notice and regulations on the front door or public bulletin board of the courthouse and certify the posting to the Commissioner. The Commissioner shall give general notice of the referendum in a newspaper of general circulation in Richmond, Virginia, and shall send a notice of the referendum to a newspaper of general circulation for each area where members of the horse industry reside, at least 60 days prior to the holding of any referendum under this chapter.</p><p>The notice shall contain: (i) the date, hours, polling place, and method of voting in such referendum; (ii) the amount of assessment to be collected, means by which such assessment shall be collected, and general purposes for which the assessments will be used; and (iii) the regulations adopted by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-112/" target="_blank" rel="noreferrer">3.2-112</a>.</p><p>C. The Commissioner shall prepare and distribute in advance of such referendum all necessary ballots, certificates, and supplies required for such referendum and shall, under regulations adopted by the Board, arrange for the use of polling places, if necessary.</p><p>D. The Commissioner shall, within 10 days after the referendum, canvass and publicly declare the results thereof and certify the same to the Governor and the Board.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0497" target="_blank" rel="noreferrer">497</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>, § 3.1-22.60; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',7),i=[o];function s(l,h,c,d,f,m){return r(),a("div",null,i)}const g=e(n,[["render",s]]);export{p as __pageData,g as default};
