import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-1719 Collection and disposition of assessment by manufacturer; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1719.md","filePath":"vacode/3.2-1719.md"}'),n={name:"vacode/3.2-1719.md"},o=r('<h1 id="§-3-2-1719-collection-and-disposition-of-assessment-by-manufacturer-report" tabindex="-1">§ 3.2-1719 Collection and disposition of assessment by manufacturer; report. <a class="header-anchor" href="#§-3-2-1719-collection-and-disposition-of-assessment-by-manufacturer-report" aria-label="Permalink to &quot;§ 3.2-1719 Collection and disposition of assessment by manufacturer; report.&quot;">​</a></h1><p>A. Every manufacturer shall collect an assessment of $3 per ton or $0.075 per 50-pound bag of manufactured equine feed he sells in the Commonwealth and on any manufactured equine feed he imports for sale in the Commonwealth and shall remit such assessment to the Department annually. The Department shall promptly pay the assessments into the state treasury to the credit of the Virginia Horse Industry Promotion and Development Fund.</p><p>B. Every manufacturer shall complete, on forms furnished by the Department, an annual report of the total tonnage of manufactured equine feed he sold and imported into the Commonwealth. Such reports shall be submitted to the Department along with the assessments submitted pursuant to subsection A. The reporting year for manufactured equine feed shall be January 1 through December 31.</p><p>C. All assessments collected under this section shall be paid to the Department by February 1 for the preceding calendar year.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0497" target="_blank" rel="noreferrer">497</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>, § 3.1-22.74; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),s=[o];function i(l,c,p,d,u,h){return t(),a("div",null,s)}const _=e(n,[["render",i]]);export{f as __pageData,_ as default};
