import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-1720 Records to be kept by manufacturer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1720.md","filePath":"vacode/3.2-1720.md"}'),o={name:"vacode/3.2-1720.md"},n=a('<h1 id="§-3-2-1720-records-to-be-kept-by-manufacturer" tabindex="-1">§ 3.2-1720 Records to be kept by manufacturer. <a class="header-anchor" href="#§-3-2-1720-records-to-be-kept-by-manufacturer" aria-label="Permalink to &quot;§ 3.2-1720 Records to be kept by manufacturer.&quot;">​</a></h1><p>Any manufacturer who is required to collect the equine feed assessment under this article shall maintain such records as may be necessary or required by the Commissioner to accurately indicate the tonnage of manufactured equine feed that he has sold or imported for sale in the Commonwealth. The manufacturer shall maintain such records for a period of not less than three years from the time the manufactured equine feed was sold. Such records shall be open to the inspection of the Commissioner.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0497" target="_blank" rel="noreferrer">497</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>, § 3.1-22.75; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',3),s=[n];function i(c,l,d,h,u,f){return r(),t("div",null,s)}const p=e(o,[["render",i]]);export{_ as __pageData,p as default};
