import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-1812 Collection of delinquent tax; civil action.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1812.md","filePath":"vacode/3.2-1812.md"}'),i={name:"vacode/3.2-1812.md"},o=n('<h1 id="§-3-2-1812-collection-of-delinquent-tax-civil-action" tabindex="-1">§ 3.2-1812 Collection of delinquent tax; civil action. <a class="header-anchor" href="#§-3-2-1812-collection-of-delinquent-tax-civil-action" aria-label="Permalink to &quot;§ 3.2-1812 Collection of delinquent tax; civil action.&quot;">​</a></h1><p>The tax imposed under the provisions of this article and unpaid on the date when due shall bear interest at a rate determined in accordance with § <a href="http://law.lis.virginia.gov/vacode/58.1-1812/" target="_blank" rel="noreferrer">58.1-1812</a>, from and after the due date until paid. If any person defaults in any payment of the tax or interest thereon, the amount shall be collected by a civil action in the name of the Commonwealth at the relation of the Board and the person adjudged in default shall pay the costs of the proceeding. The Attorney General, at the request of the Commissioner, shall institute an appropriate action for the collection of the amount of any tax past due under this article, including interest thereon.</p><p>1982, c. 126, § 3.1-684.35; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),r=[o];function l(c,d,s,h,f,p){return t(),a("div",null,r)}const g=e(i,[["render",l]]);export{u as __pageData,g as default};
