import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-1818 Inspection of potatoes; right of entry; fees; records required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1818.md","filePath":"vacode/3.2-1818.md"}'),a={name:"vacode/3.2-1818.md"},s=r('<h1 id="§-3-2-1818-inspection-of-potatoes-right-of-entry-fees-records-required" tabindex="-1">§ 3.2-1818 Inspection of potatoes; right of entry; fees; records required. <a class="header-anchor" href="#§-3-2-1818-inspection-of-potatoes-right-of-entry-fees-records-required" aria-label="Permalink to &quot;§ 3.2-1818 Inspection of potatoes; right of entry; fees; records required.&quot;">​</a></h1><p>A. The Commissioner shall inspect any seed potatoes. The Commissioner may enter any place of business, warehouse, common carrier, or other place where seed potatoes may be found for the purpose of an inspection. It is unlawful for any person to interfere with such inspections.</p><p>B. The fee for inspection shall not exceed the lesser of the current rate for federal-state inspection of table stock potatoes or the reasonable cost of inspection. The Commissioner shall abate any fee to the extent funds are appropriated from the general fund for seed potato inspection.</p><p>C. Bills of lading, invoices, or other records accompanying any shipment of approved seed potatoes shall give the name of the consignee, consignor, and custodian, if any. The Commissioner shall have the right to inspect such records.</p><p>Code 1950, § 3-228.8; 1950, p. 1030; 1966, cc. 686, 702, § 3.1-292; 1978, c. 540; 1980, c. 308; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>, § 3.2-4107; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',5),n=[s];function i(c,p,f,h,l,d){return o(),t("div",null,n)}const u=e(a,[["render",i]]);export{g as __pageData,u as default};
