import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-1904 Powers and duties of Peanut Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-1904.md","filePath":"vacode/3.2-1904.md"}'),n={name:"vacode/3.2-1904.md"},o=r('<h1 id="§-3-2-1904-powers-and-duties-of-peanut-board" tabindex="-1">§ 3.2-1904 Powers and duties of Peanut Board. <a class="header-anchor" href="#§-3-2-1904-powers-and-duties-of-peanut-board" aria-label="Permalink to &quot;§ 3.2-1904 Powers and duties of Peanut Board.&quot;">​</a></h1><p>A. All funds levied and collected under this chapter shall be administered by the Peanut Board.</p><p>B. The Peanut Board shall plan and conduct campaigns for education, advertising, publicity, sales promotion, and research as to Virginia peanuts.</p><p>C. The Peanut Board may make contracts, expend moneys of the Peanut Fund, and do whatever else may be necessary to effectuate the purposes of this chapter.</p><p>D. The Peanut Board may cooperate with other state, regional, and national agricultural and peanut organizations in research, advertising, publicity, education, and other means of promoting the sale and use of peanuts, and may expend moneys of the Peanut Fund for such purposes.</p><p>E. The Peanut Board may enter into an agreement with the Federal Commodity Credit Corporation or its designee to collect and remit the specified assessment on all peanuts pledged as collateral for a marketing assistance or price support loan.</p><p>F. The chairman shall make a report at the annual meeting of the Peanut Board and furnish the members of the Peanut Board with a statement of the total receipts and disbursements for the year. He shall file a copy of the report and the audit required by § <a href="http://law.lis.virginia.gov/vacode/3.2-1906/" target="_blank" rel="noreferrer">3.2-1906</a> with the Commissioner.</p><p>Code 1950, §§ 3-525.4, 3-525.6 to 3-525.10; 1966, c. 702, §§ 3.1-650, 3.1-652 to 3.1-656; 1978, c. 540; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0007" target="_blank" rel="noreferrer">7</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0565" target="_blank" rel="noreferrer">565</a>.</p>',8),i=[o];function s(d,l,p,c,h,u){return a(),t("div",null,i)}const g=e(n,[["render",s]]);export{m as __pageData,g as default};
