import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-2007 Collection and disposition of tax; reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-2007.md","filePath":"vacode/3.2-2007.md"}'),r={name:"vacode/3.2-2007.md"},i=a('<h1 id="§-3-2-2007-collection-and-disposition-of-tax-reports" tabindex="-1">§ 3.2-2007 Collection and disposition of tax; reports. <a class="header-anchor" href="#§-3-2-2007-collection-and-disposition-of-tax-reports" aria-label="Permalink to &quot;§ 3.2-2007 Collection and disposition of tax; reports.&quot;">​</a></h1><p>A. Every processor shall collect the tax on all slaughter hogs purchased by him, other than at a livestock auction market, and remit such tax to the Tax Commissioner to the credit of the Virginia Pork Industry Fund.</p><p>B. Every livestock auction market shall collect the tax on all slaughter hogs and feeder pigs bought at a livestock auction market and remit it to the Tax Commissioner to the credit of the Virginia Pork Industry Fund.</p><p>C. Every first buyer shall collect the tax on other feeder pig sales and remit it to the Tax Commissioner to the credit of the Virginia Pork Industry Fund.</p><p>D. Taxes collected between July 1 and December 31 of each year shall be paid to the Tax Commissioner not later than January 15 of the succeeding year, and taxes collected between January 1 and June 30 shall be paid not later than July 10 of the year in which collected.</p><p>E. Every processor, livestock auction market, and other first buyers of feeder pigs shall complete reports on forms furnished by the Tax Commissioner, submit such reports to the Tax Commissioner together with the taxes submitted pursuant to this section, and keep copies of the reports for a period of not less than three years from the time the report was produced.</p><p>F. The Tax Commissioner, after consultation with the Virginia Pork Industry Board, may adopt regulations for the interpretation and enforcement of this tax.</p><p>G. Any tax that is not paid when due shall be collected pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-1102/" target="_blank" rel="noreferrer">3.2-1102</a>.</p><p>Code 1950, § 3-598.15; 1966, c. 658, § 3.1-763.9; 1979, c. 72; 1982, c. 99; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',9),s=[i];function n(l,c,h,d,p,u){return t(),o("div",null,s)}const _=e(r,[["render",n]]);export{m as __pageData,_ as default};
