import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-2104 Powers and duties of Sheep Industry Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-2104.md","filePath":"vacode/3.2-2104.md"}'),o={name:"vacode/3.2-2104.md"},i=r('<h1 id="§-3-2-2104-powers-and-duties-of-sheep-industry-board" tabindex="-1">§ 3.2-2104 Powers and duties of Sheep Industry Board. <a class="header-anchor" href="#§-3-2-2104-powers-and-duties-of-sheep-industry-board" aria-label="Permalink to &quot;§ 3.2-2104 Powers and duties of Sheep Industry Board.&quot;">​</a></h1><p>A. The Sheep Industry Board shall be responsible for the promotion and economic development of the sheep industry in the Commonwealth. To accomplish this function the Sheep Industry Board is authorized to:</p><ol><li><p>Provide funding for predator control;</p></li><li><p>Produce economic reports;</p></li><li><p>Develop a sheep industry directory;</p></li><li><p>Provide funding for educational programs;</p></li><li><p>Provide funding for research;</p></li><li><p>Engage in media liaison;</p></li><li><p>Collect and analyze data on the sheep industry;</p></li><li><p>Disseminate industry-related data;</p></li><li><p>Enter into contracts and agreements to accomplish the purposes of this chapter; and</p></li><li><p>Establish, administer, manage, and make expenditures from the Virginia Sheep Industry Promotion and Development Fund as provided in § <a href="http://law.lis.virginia.gov/vacode/3.2-2111/" target="_blank" rel="noreferrer">3.2-2111</a>.</p></li></ol><p>B. The Sheep Industry Board may increase the original assessment of 50 cents ($0.50) for each sheep sold within the Commonwealth no more than 10 cents ($0.10) per year, up to a maximum assessment of $1 per head.</p><p>C. The chairman of the Sheep Industry Board shall make a report at the annual meeting of the Sheep Industry Board including a statement of the total receipts and disbursements for the year, and shall file a copy of the report with the Commissioner.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0691" target="_blank" rel="noreferrer">691</a>, §§ 3.1-1066, 3.1-1074; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',6),n=[i];function s(d,p,l,h,c,m){return a(),t("div",null,n)}const _=e(o,[["render",s]]);export{f as __pageData,_ as default};
