import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse(`{"title":"§ 3.2-2106 Management of referenda; Commissioner's duties; notice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-2106.md","filePath":"vacode/3.2-2106.md"}`),o={name:"vacode/3.2-2106.md"},n=r('<h1 id="§-3-2-2106-management-of-referenda-commissioner-s-duties-notice" tabindex="-1">§ 3.2-2106 Management of referenda; Commissioner&#39;s duties; notice. <a class="header-anchor" href="#§-3-2-2106-management-of-referenda-commissioner-s-duties-notice" aria-label="Permalink to &quot;§ 3.2-2106 Management of referenda; Commissioner&#39;s duties; notice.&quot;">​</a></h1><p>A. The Commissioner shall manage any referendum conducted under this chapter, and shall, under regulations adopted by the Board, arrange for the use of polling places, if necessary.</p><p>B. The Commissioner shall, at least 60 days before the date upon which a referendum is to be held, mail notice to the clerk of the circuit court in each locality where those eligible to vote in the referendum reside. The clerk of the circuit court shall post the notice and regulations on the front door or public bulletin board of the courthouse and certify the posting to the Commissioner. The Commissioner shall also give general notice of the referendum in a newspaper of general circulation in Richmond, Virginia, and shall send a notice of the referendum to a newspaper of general circulation in each area where members of the sheep industry reside, at least 60 days prior to the holding of any referendum under this chapter.</p><p>The notice shall contain the date, hours, voting places, and methods of voting in the referendum; the amount of assessment to be collected, means by which such assessment will be collected, and general purposes for how the assessments will be used; and the regulations adopted by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-112/" target="_blank" rel="noreferrer">3.2-112</a>.</p><p>C. The Commissioner shall prepare and distribute in advance of the referendum all necessary ballots, certificates, and supplies required for the referendum.</p><p>D. The Commissioner shall within 10 days after the referendum, canvass and publicly declare the results of the referendum, and certify the same to the Governor and the Board.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0691" target="_blank" rel="noreferrer">691</a>, §§ 3.1-1069, 3.1-1070; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',7),s=[n];function i(l,d,h,c,f,m){return t(),a("div",null,s)}const g=e(o,[["render",i]]);export{p as __pageData,g as default};
