import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 3.2-2405 Powers and duties of the Tobacco Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-2405.md","filePath":"vacode/3.2-2405.md"}'),t={name:"vacode/3.2-2405.md"},n=r('<h1 id="§-3-2-2405-powers-and-duties-of-the-tobacco-board" tabindex="-1">§ 3.2-2405 Powers and duties of the Tobacco Board. <a class="header-anchor" href="#§-3-2-2405-powers-and-duties-of-the-tobacco-board" aria-label="Permalink to &quot;§ 3.2-2405 Powers and duties of the Tobacco Board.&quot;">​</a></h1><p>A. All funds levied and collected under this chapter shall be administered by the Tobacco Board.</p><p>B. The Tobacco Board shall plan and conduct campaigns of education, advertising, publicity, sales promotion, and research to increase the demand for, and the consumption of, bright flue-cured and type 21 dark-fired tobaccos.</p><p>C. The Tobacco Board may make contracts, expend moneys of the Bright Flue-Cured Tobacco Promotion Fund and the Dark-Fired Tobacco Promotion Fund, and do whatever else may be necessary to effectuate the purposes of this chapter.</p><p>D. The Tobacco Board may cooperate with other state, regional, and national agricultural organizations in research, advertising, publicity, education, and other means of promoting the sale, use, and exportation of bright flue-cured and type 21 dark-fired tobaccos, and expend moneys of the Bright Flue-Cured Tobacco Promotion Fund and the Dark-Fired Tobacco Promotion Fund for such purposes.</p><p>E. The Chairman shall make a report at the annual meeting of the Tobacco Board and furnish members with a statement of the total receipts and disbursements for the year. He shall file a copy of such report and the audit required by § <a href="http://law.lis.virginia.gov/vacode/3.2-2407/" target="_blank" rel="noreferrer">3.2-2407</a> with the Commissioner.</p><p>Code 1950, §§ 3-245 to 3-248; 1966, c. 702, §§ 3.1-324 to 3.1-327; 1978, c. 540; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0008" target="_blank" rel="noreferrer">8</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0066" target="_blank" rel="noreferrer">66</a>.</p>',7),i=[n];function c(d,s,l,h,p,f){return a(),o("div",null,i)}const g=e(t,[["render",c]]);export{b as __pageData,g as default};
