import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-2601 Powers and duties of Commissioner.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-2601.md","filePath":"vacode/3.2-2601.md"}'),o={name:"vacode/3.2-2601.md"},n=t('<h1 id="§-3-2-2601-powers-and-duties-of-commissioner" tabindex="-1">§ 3.2-2601 Powers and duties of Commissioner. <a class="header-anchor" href="#§-3-2-2601-powers-and-duties-of-commissioner" aria-label="Permalink to &quot;§ 3.2-2601 Powers and duties of Commissioner.&quot;">​</a></h1><p>The Commissioner shall have the following powers and duties:</p><ol><li><p>To provide information and assistance in obtaining permits relating to aquacultural activities;</p></li><li><p>To promote aquaculture including encouraging investment in aquaculture facilities to expand production, processing capacity, and marketing;</p></li><li><p>To work with appropriate state and federal agencies to review, develop, and implement policies and procedures to facilitate aquacultural development;</p></li><li><p>To consult with and assist aquaculture industry groups, aquaculture associations, and academic institutions to develop, maintain, and expand the aquaculture industry; and</p></li><li><p>To develop, support, and implement policies beneficial to Virginia aquaculture.</p></li></ol><p>1992, c. 643, § 3.1-73.7; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0320" target="_blank" rel="noreferrer">320</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),r=[n];function s(l,c,d,p,u,m){return a(),i("div",null,r)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
