import{_ as e,o,c as i,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-2805 Powers and duties of Commissioner.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-2805.md","filePath":"vacode/3.2-2805.md"}'),a={name:"vacode/3.2-2805.md"},t=r('<h1 id="§-3-2-2805-powers-and-duties-of-commissioner" tabindex="-1">§ 3.2-2805 Powers and duties of Commissioner. <a class="header-anchor" href="#§-3-2-2805-powers-and-duties-of-commissioner" aria-label="Permalink to &quot;§ 3.2-2805 Powers and duties of Commissioner.&quot;">​</a></h1><p>The Commissioner shall have the following powers and duties:</p><ol><li><p>To receive and dispense funds;</p></li><li><p>To develop and administer a beekeeper assistance program that is designed to assist Virginia beekeepers in maintaining healthy, productive colonies;</p></li><li><p>To enter into contracts for the purpose of developing new or improved markets or marketing methods for bees and bee products and pollination services;</p></li><li><p>To contract for scientific research services and to contract to develop improved pollinating behavior in honeybees and other pollinating insects;</p></li><li><p>To contract for rearing numbers of improved queen bees sufficient for distribution or sale to beekeepers;</p></li><li><p>To enter into agreements with any local, state or national organization or agency engaged in education for the purpose of disseminating information on pollinators and pollination of crops;</p></li><li><p>To rent or purchase office and laboratory space and land as necessary to carry out its duties;</p></li><li><p>To appoint employees, full- or part-time, and to fix their compensation, if any, in accord with the provisions of the Virginia Personnel Act, (§ <a href="http://law.lis.virginia.gov/vacode/2.2-2900/" target="_blank" rel="noreferrer">2.2-2900</a> et seq.);</p></li><li><p>To encourage research, education, methods of improvement in apicultural practices, and promotion projects and the award of funds for such projects as deemed necessary or advisable to accomplish the objectives set forth in this chapter; and</p></li><li><p>To report to the Board in the manner and at such times as the Board may prescribe, regarding the receipt and expenditure of funds. The Commissioner shall ensure that funds made available to the Plant Pollination Fund are expended only for the purposes authorized by this chapter.</p></li></ol><p>1977, c. 452, §§ 3.1-610.25, 3.1-610.26:1; 1978, c. 267; 1992, c. 121; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0201" target="_blank" rel="noreferrer">201</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0594" target="_blank" rel="noreferrer">594</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>.</p>',4),n=[t];function s(l,p,c,d,h,f){return o(),i("div",null,n)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
