import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 3.2-2904 Powers and duties of the Agricultural Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-2904.md","filePath":"vacode/3.2-2904.md"}'),n={name:"vacode/3.2-2904.md"},o=t('<h1 id="§-3-2-2904-powers-and-duties-of-the-agricultural-council" tabindex="-1">§ 3.2-2904 Powers and duties of the Agricultural Council. <a class="header-anchor" href="#§-3-2-2904-powers-and-duties-of-the-agricultural-council" aria-label="Permalink to &quot;§ 3.2-2904 Powers and duties of the Agricultural Council.&quot;">​</a></h1><p>A. The Agricultural Council shall have charge of the management and expenditure of the Virginia Agricultural Foundation Fund. The Agricultural Council may expend funds to provide for programs of agricultural research and education and agricultural services; manage the fund so as to accumulate a reserve for contingencies; establish an office and employ such technical, professional, and other assistants as may be required, subject to the provisions of the Virginia Personnel Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-2900/" target="_blank" rel="noreferrer">2.2-2900</a> et seq.); contract for research and other services; and take all such measures as will assist in strengthening and promoting the best interests of agriculture in the Commonwealth.</p><p>B. The chairman shall submit an annual report to the members, the Governor, and the General Assembly on or before November 1 of each year. The report shall contain the annual financial statements of the Agricultural Council for the year ending the preceding June 30.</p><p>Code 1950, § 3-16.8; 1966, c. 536, § 3.1-22.4; 1979, c. 69; 1984, c. 734; 1985, cc. 146, 448; 1992, c. 121; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0650" target="_blank" rel="noreferrer">650</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),i=[o];function s(c,l,u,h,d,f){return a(),r("div",null,i)}const _=e(n,[["render",s]]);export{p as __pageData,_ as default};
