import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-3003 Wine Board officers and compensation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3003.md","filePath":"vacode/3.2-3003.md"}'),n={name:"vacode/3.2-3003.md"},o=i('<h1 id="§-3-2-3003-wine-board-officers-and-compensation" tabindex="-1">§ 3.2-3003 Wine Board officers and compensation. <a class="header-anchor" href="#§-3-2-3003-wine-board-officers-and-compensation" aria-label="Permalink to &quot;§ 3.2-3003 Wine Board officers and compensation.&quot;">​</a></h1><p>A. The Wine Board shall elect a chairman and other officers as deemed necessary from among its membership.</p><p>B. Members of the Wine Board shall receive no compensation for the discharge of their duties but the nonlegislative citizen members shall be reimbursed for reasonable and necessary expenses incurred in the discharge of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. Funding for expenses of the nonlegislative citizen members shall be provided from the Virginia Wine Promotion Fund established under § <a href="http://law.lis.virginia.gov/vacode/3.2-3005/" target="_blank" rel="noreferrer">3.2-3005</a>.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0319" target="_blank" rel="noreferrer">319</a>, § 3.1-1064.3; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),t=[o];function s(c,l,d,f,h,p){return a(),r("div",null,t)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
