import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-3004 Powers and duties of the Wine Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3004.md","filePath":"vacode/3.2-3004.md"}'),i={name:"vacode/3.2-3004.md"},a=t('<h1 id="§-3-2-3004-powers-and-duties-of-the-wine-board" tabindex="-1">§ 3.2-3004 Powers and duties of the Wine Board. <a class="header-anchor" href="#§-3-2-3004-powers-and-duties-of-the-wine-board" aria-label="Permalink to &quot;§ 3.2-3004 Powers and duties of the Wine Board.&quot;">​</a></h1><p>The Wine Board shall have the following powers and duties:</p><ol><li><p>To receive and dispense funds or donations from the Virginia Wine Promotion Fund;</p></li><li><p>To enter into contracts for the purpose of developing new or improved markets or marketing methods for wine and grape products;</p></li><li><p>To contract for research services to improve viticulture and enological practices in Virginia;</p></li><li><p>To enter into agreements with any local, state, or national organization or agency engaged in education for the purpose of disseminating information on wine or other viticulture projects;</p></li><li><p>To enter into contracts with private or public entities for the purpose of developing marketing, advertising and other promotional programs designed to promote the orderly growth of Virginia&#39;s wine industry;</p></li><li><p>To rent or purchase office and laboratory space, land, equipment, and supplies as necessary to carry out its duties;</p></li><li><p>To employ such personnel as may be required to carry out those duties conferred by law;</p></li><li><p>To acquire any licenses or permits necessary for the performance of the powers and duties of the Wine Board;</p></li><li><p>To cooperate with other state, regional, national, and international organizations in research, education, and promotion of the growing of grapes and the production of wine in the Commonwealth and to expend moneys from the Fund for such purposes;</p></li><li><p>To adopt a general statement of policy and procedures; and</p></li><li><p>To receive from the Chairman of the Wine Board an annual report, including a statement of total receipts and disbursements for the year, and file a copy of such report with the Commissioner.</p></li></ol><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0319" target="_blank" rel="noreferrer">319</a>, § 3.1-1064.5; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),n=[a];function s(p,d,l,c,h,f){return o(),r("div",null,n)}const m=e(i,[["render",s]]);export{g as __pageData,m as default};
