import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-3010 Board officers and compensation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3010.md","filePath":"vacode/3.2-3010.md"}'),i={name:"vacode/3.2-3010.md"},n=o('<h1 id="§-3-2-3010-board-officers-and-compensation" tabindex="-1">§ 3.2-3010 Board officers and compensation. <a class="header-anchor" href="#§-3-2-3010-board-officers-and-compensation" aria-label="Permalink to &quot;§ 3.2-3010 Board officers and compensation.&quot;">​</a></h1><p>A. The Board shall elect a chairman and other officers as deemed necessary from among its membership.</p><p>B. Members of the Board shall receive no compensation for the discharge of their duties, but the nonlegislative citizen members shall be reimbursed for reasonable and necessary expenses incurred in the discharge of their duties as provided in §§ <a href="/vacode/2.2-2813/">2.2-2813</a> and <a href="/vacode/2.2-2825/">2.2-2825</a>. Funding for expenses of the nonlegislative citizen members shall be provided from the Virginia Spirits Promotion Fund established under § <a href="/vacode/3.2-3012/">3.2-3012</a>.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0085" target="_blank" rel="noreferrer">85</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>.</p>',4),t=[n];function s(c,d,f,l,h,p){return a(),r("div",null,t)}const b=e(i,[["render",s]]);export{m as __pageData,b as default};
