import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-3011 Powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3011.md","filePath":"vacode/3.2-3011.md"}'),r={name:"vacode/3.2-3011.md"},a=i('<h1 id="§-3-2-3011-powers-and-duties-of-the-board" tabindex="-1">§ 3.2-3011 Powers and duties of the Board. <a class="header-anchor" href="#§-3-2-3011-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 3.2-3011 Powers and duties of the Board.&quot;">​</a></h1><p>The Board shall have the power and duty to:</p><ol><li><p>Receive and dispense funds or donations from the Virginia Spirits Promotion Fund;</p></li><li><p>Enter into contracts for the purpose of developing new or improved markets or marketing methods for spirits products;</p></li><li><p>Contract for research services to improve farming practices related to the growing of ingredients necessary for alcohol distillation in Virginia;</p></li><li><p>Enter into agreements with any local, state, or national organization or agency engaged in education for the purpose of disseminating information on spirits projects;</p></li><li><p>Enter into contracts with private or public entities for the purpose of developing marketing, advertising, and other promotional programs designed to promote the orderly growth of Virginia&#39;s spirits industry;</p></li><li><p>Rent or purchase office and laboratory space, land, equipment, and supplies as necessary to carry out its duties;</p></li><li><p>Employ such personnel as may be required to carry out those duties conferred by law;</p></li><li><p>Acquire any licenses or permits necessary for the performance of the powers and duties of the Board;</p></li><li><p>Cooperate with other state, regional, national, and international organizations in research, education, and promotion of the growing of ingredients necessary for alcohol production and the production of spirits in the Commonwealth and expend moneys from the Fund for such purposes;</p></li><li><p>Adopt a general statement of policy and procedures;</p></li><li><p>Receive from the chairman of the Board an annual report, including a statement of total receipts and disbursements for the year, and file a copy of such report with the Commissioner; and</p></li><li><p>Submit an annual report to the Governor and General Assembly by October 1 of each year regarding the Board&#39;s activities and use of moneys in the Virginia Spirits Promotion Fund, including statistics regarding the extent to which use of moneys in the Virginia Spirits Promotion Fund has impacted spirits sales in the Commonwealth, which shall be submitted for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports and shall be posted on the General Assembly&#39;s website.</p></li></ol><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0085" target="_blank" rel="noreferrer">85</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0084" target="_blank" rel="noreferrer">84</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0085" target="_blank" rel="noreferrer">85</a>.</p>',4),n=[a];function s(p,l,d,c,h,f){return o(),t("div",null,n)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
