import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-3105 Appointment of director; Commission employees; counsel to the Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3105.md","filePath":"vacode/3.2-3105.md"}'),r={name:"vacode/3.2-3105.md"},a=i('<h1 id="§-3-2-3105-appointment-of-director-commission-employees-counsel-to-the-commission" tabindex="-1">§ 3.2-3105 Appointment of director; Commission employees; counsel to the Commission. <a class="header-anchor" href="#§-3-2-3105-appointment-of-director-commission-employees-counsel-to-the-commission" aria-label="Permalink to &quot;§ 3.2-3105 Appointment of director; Commission employees; counsel to the Commission.&quot;">​</a></h1><p>A. The Governor shall appoint an executive director subject to confirmation by the General Assembly. The compensation shall be determined by the Commission, subject to approval by the Governor. The executive director shall serve as the secretary to the Commission and shall administer the affairs and business of the Commission in accordance with the provisions of this chapter and subject to the policies, control, and direction of the Commission. The Commission may employ technical experts and other officers, agents, and employees, permanent and temporary, as it requires, and shall determine their qualifications, duties, and compensation. The Commission may delegate to one or more of its agents or employees the administrative duties it deems proper. The actual expenses incurred in the performance of such duties shall be paid from the Fund.</p><p>B. Employees of the Commission shall be treated as state employees for purposes of participation in the Virginia Retirement System, health insurance, and all other employee benefits offered by the Commonwealth to its classified employees. Employees of the Commission shall not be subject to the provisions of Chapter 29 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-2900/" target="_blank" rel="noreferrer">2.2-2900</a> et seq.) of Title 2.2.</p><p>C. The Office of the Attorney General shall provide counsel to the Commission.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0962" target="_blank" rel="noreferrer">962</a>, § 9-384; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>, § 3.1-1110; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0482" target="_blank" rel="noreferrer">482</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0488" target="_blank" rel="noreferrer">488</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),s=[a];function n(l,m,c,p,h,f){return o(),t("div",null,s)}const u=e(r,[["render",n]]);export{g as __pageData,u as default};
