import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 3.2-3110 Declaration of public purpose; exemption from taxation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3110.md","filePath":"vacode/3.2-3110.md"}'),a={name:"vacode/3.2-3110.md"},i=r('<h1 id="§-3-2-3110-declaration-of-public-purpose-exemption-from-taxation" tabindex="-1">§ 3.2-3110 Declaration of public purpose; exemption from taxation. <a class="header-anchor" href="#§-3-2-3110-declaration-of-public-purpose-exemption-from-taxation" aria-label="Permalink to &quot;§ 3.2-3110 Declaration of public purpose; exemption from taxation.&quot;">​</a></h1><p>A. The exercise of the powers granted by this chapter shall be in all respects for the benefit of the citizens of the Commonwealth and for the promotion of their welfare, convenience, and prosperity.</p><p>B. The Commission shall be performing an essential governmental function in the exercise of the powers conferred upon it by this chapter, and the property of the Commission and its income and operations shall be exempt from taxation or assessments upon any property acquired or used by the Commission under the provisions of this chapter.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0962" target="_blank" rel="noreferrer">962</a>, § 9-389; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>, § 3.1-1114; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),n=[i];function p(s,c,l,f,h,m){return t(),o("div",null,n)}const u=e(a,[["render",p]]);export{d as __pageData,u as default};
