import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-3114 Deposit of money; expenditures; investments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3114.md","filePath":"vacode/3.2-3114.md"}'),i={name:"vacode/3.2-3114.md"},a=n('<h1 id="§-3-2-3114-deposit-of-money-expenditures-investments" tabindex="-1">§ 3.2-3114 Deposit of money; expenditures; investments. <a class="header-anchor" href="#§-3-2-3114-deposit-of-money-expenditures-investments" aria-label="Permalink to &quot;§ 3.2-3114 Deposit of money; expenditures; investments.&quot;">​</a></h1><p>A. All money belonging to the Fund shall be deposited in an account or accounts in banks or trust companies organized under the laws of the Commonwealth, national banking associations located in the Commonwealth, or savings institutions located in the Commonwealth and organized under the laws of the Commonwealth or the United States. The money in these accounts shall be paid by check signed by the Executive Director of the Authority or another officer or employee designated by the Board of Directors of the Authority. All deposits of money shall, if required by the Authority, be secured in a manner determined by the Authority to be prudent, and all banks, trust companies, and savings institutions are authorized to give security for the deposits.</p><p>B. Money in the Fund shall not be commingled with other money of the Authority. Money in the Fund not needed for immediate use or disbursement may be invested or reinvested by the Authority in obligations or securities that are considered lawful investments for public funds under the laws of the Commonwealth, including the Local Government Investment Pool Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4600/" target="_blank" rel="noreferrer">2.2-4600</a> et seq.).</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0399" target="_blank" rel="noreferrer">399</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0433" target="_blank" rel="noreferrer">433</a>.</p>',4),r=[a];function s(d,h,l,c,m,u){return t(),o("div",null,r)}const _=e(i,[["render",s]]);export{f as __pageData,_ as default};
