import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 3.2-3115 Collection of money due the Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3115.md","filePath":"vacode/3.2-3115.md"}'),r={name:"vacode/3.2-3115.md"},n=a('<h1 id="§-3-2-3115-collection-of-money-due-the-fund" tabindex="-1">§ 3.2-3115 Collection of money due the Fund. <a class="header-anchor" href="#§-3-2-3115-collection-of-money-due-the-fund" aria-label="Permalink to &quot;§ 3.2-3115 Collection of money due the Fund.&quot;">​</a></h1><p>The Authority is empowered to collect, or to authorize others to collect on its behalf, amounts due to the Fund under any loan to a local government, including, if appropriate, by taking the action required by § <a href="http://law.lis.virginia.gov/vacode/15.2-2659/" target="_blank" rel="noreferrer">15.2-2659</a> or <a href="http://law.lis.virginia.gov/vacode/62.1-216.1/" target="_blank" rel="noreferrer">62.1-216.1</a> to obtain payment of any amounts in default. Proceedings to recover amounts due to the Fund may be instituted by the Authority in the name of the Fund in the appropriate circuit court.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0399" target="_blank" rel="noreferrer">399</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0433" target="_blank" rel="noreferrer">433</a>.</p>',3),i=[n];function l(c,d,h,u,s,_){return t(),o("div",null,i)}const m=e(r,[["render",l]]);export{p as __pageData,m as default};
