import{_ as t,o,c as n,z as e,a as s}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 3.2-3219 Annual budget; assessment of distributors and producers; bond requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3219.md","filePath":"vacode/3.2-3219.md"}'),r={name:"vacode/3.2-3219.md"},a=e("h1",{id:"§-3-2-3219-annual-budget-assessment-of-distributors-and-producers-bond-requirements",tabindex:"-1"},[s("§ 3.2-3219 Annual budget; assessment of distributors and producers; bond requirements. "),e("a",{class:"header-anchor",href:"#§-3-2-3219-annual-budget-assessment-of-distributors-and-producers-bond-requirements","aria-label":'Permalink to "§ 3.2-3219 Annual budget; assessment of distributors and producers; bond requirements."'},"​")],-1),i=e("p",null,"The Commission shall prepare an annual budget and shall collect the sums of money required for this budget from the licensed distributors in markets where the provisions of this chapter are in operation. The expenses of the Commission, including salaries and the per diem of such personnel as the Commission finds it necessary to employ to properly carry out its functions under this chapter shall be met by an assessment of not over five cents ($0.05) per 100 pounds of milk, and cream (converted to terms of milk) handled by distributors and not over five cents ($0.05) per 100 pounds of milk, and cream (converted to terms of milk) sold by producers; these assessments to be the same per 100 pounds on producers and distributors. The exact amount of each monthly or semimonthly assessment shall be determined by the Commission as necessary to cover its expenses. All assessments shall be paid at the time the distributors pay the producers for the milk. All officers and employees of the Commission, who handle funds of the Commission or who sign or countersign checks upon such funds, shall severally give bond in such amount and with such sureties as shall be determined by the Commission. The cost of the bonds shall be paid by the Commission and the Commission shall determine the amount and sufficiency of the bonds.",-1),d=e("p",null,[s("Code 1950, § 3-373; 1958, c. 110; 1966, c. 702, § 3.1-452; 1974, c. 467; 1986, c. 408; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),s(".")],-1),l=[a,i,d];function c(m,h,u,p,f,b){return o(),n("div",null,l)}const g=t(r,[["render",c]]);export{y as __pageData,g as default};
