import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-3607.1 Consumer education.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3607.1.md","filePath":"vacode/3.2-3607.1.md"}'),n={name:"vacode/3.2-3607.1.md"},i=r('<h1 id="§-3-2-3607-1-consumer-education" tabindex="-1">§ 3.2-3607.1 Consumer education. <a class="header-anchor" href="#§-3-2-3607-1-consumer-education" aria-label="Permalink to &quot;§ 3.2-3607.1 Consumer education.&quot;">​</a></h1><p>A. The Department, in consultation with representatives of the fertilizer industry, fertilizer retailers, and statewide turf and lawn care organizations, and other interested parties, may develop consumer information and recommended best practices for the application of lawn fertilizer.</p><p>B. The Department shall provide a public listing of contractor-applicators who apply fertilizer on nonagricultural lands and have met the training requirements of § <a href="http://law.lis.virginia.gov/vacode/3.2-3602.1/" target="_blank" rel="noreferrer">3.2-3602.1</a>. The Department shall encourage consumers to consult the listing when hiring a lawn care professional.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0341" target="_blank" rel="noreferrer">341</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>.</p>',4),o=[i];function s(c,l,d,p,_,h){return a(),t("div",null,o)}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
