import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 3.2-3607.2 Sale of deicing agents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3607.2.md","filePath":"vacode/3.2-3607.2.md"}'),n={name:"vacode/3.2-3607.2.md"},r=o('<h1 id="§-3-2-3607-2-sale-of-deicing-agents" tabindex="-1">§ 3.2-3607.2 Sale of deicing agents. <a class="header-anchor" href="#§-3-2-3607-2-sale-of-deicing-agents" aria-label="Permalink to &quot;§ 3.2-3607.2 Sale of deicing agents.&quot;">​</a></h1><p>Beginning December 31, 2013, it is unlawful for any person to offer for sale any deicing agent containing urea or other forms of nitrogen or phosphorus intended for application to parking lots, roadways, and sidewalks or other paved surfaces in the Commonwealth. The provisions of this section shall not (i) restrict the continued sale by retailers of any deicing agent from any existing inventories in stock on December 31, 2013, or (ii) prohibit the offer for sale or sale of any deicing agents containing urea to any municipal corporation or political subdivision for the purpose of applying such deicing agents pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/15.2-1123/" target="_blank" rel="noreferrer">15.2-1123</a>.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0341" target="_blank" rel="noreferrer">341</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',3),t=[r];function s(c,l,g,f,p,d){return a(),i("div",null,t)}const u=e(n,[["render",s]]);export{h as __pageData,u as default};
