import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-3609 Reporting year; inspection fees; distribution to nonlicensees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3609.md","filePath":"vacode/3.2-3609.md"}'),o={name:"vacode/3.2-3609.md"},r=n('<h1 id="§-3-2-3609-reporting-year-inspection-fees-distribution-to-nonlicensees" tabindex="-1">§ 3.2-3609 Reporting year; inspection fees; distribution to nonlicensees. <a class="header-anchor" href="#§-3-2-3609-reporting-year-inspection-fees-distribution-to-nonlicensees" aria-label="Permalink to &quot;§ 3.2-3609 Reporting year; inspection fees; distribution to nonlicensees.&quot;">​</a></h1><p>A. The reporting year for regulated products shall be July 1 through June 30 of the following year.</p><p>B. Any person who distributes any regulated product to a non-licensed person:</p><ol><li><p>Shall file the tonnage statement with the Commissioner and pay to the Commissioner the inspection fee by August 1; or</p></li><li><p>Shall not be required to file the tonnage statement or pay the inspection fee, if: (i) another person agrees in a written statement, filed with the Commissioner, to file the tonnage statement and to pay to the Commissioner the inspection fee by August 1; and (ii) he files with the Commissioner by August 1 on a form furnished by the Commissioner a purchasing report stating the number of tons of regulated product purchased by the person during the reporting year and from whom the regulated product was purchased.</p></li></ol><p>C. Any person who distributes any regulated product in Virginia to a nonlicensee shall pay to the Commissioner an inspection fee of 25 cents ($0.25) per ton of regulated product or $35, whichever is greater, per tonnage reporting year.</p><p>D. Any person who distributes any regulated product to a nonlicensee shall pay to the Commissioner a late fee, amounting to 10 percent of the inspection fee due, or $50, whichever is greater, in addition to the amount of the inspection fee due, if the tonnage statement is not filed, is misstated, or if the payment of inspection fees is not made within 15 working days of the specified filing date.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>, § 3.1-106.8; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0564" target="_blank" rel="noreferrer">564</a>.</p>',7),a=[r];function s(p,l,h,c,d,f){return t(),i("div",null,a)}const m=e(o,[["render",s]]);export{u as __pageData,m as default};
