import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-3610 Statistical reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3610.md","filePath":"vacode/3.2-3610.md"}'),o={name:"vacode/3.2-3610.md"},a=r('<h1 id="§-3-2-3610-statistical-reports" tabindex="-1">§ 3.2-3610 Statistical reports. <a class="header-anchor" href="#§-3-2-3610-statistical-reports" aria-label="Permalink to &quot;§ 3.2-3610 Statistical reports.&quot;">​</a></h1><p>A. For commercial fertilizer:</p><ol><li>Any person distributing commercial fertilizer to a nonlicensee shall furnish the Commissioner an annual statistical report showing:</li></ol><p>a. The county or city of the nonlicensee consignee;</p><p>b. The amounts (expressed in tons, or decimal portions) of each grade of fertilizer; and</p><p>c. The form in which the person distributed the fertilizer (e.g., in bags, bulk, or in liquid form).</p><ol start="2"><li>This information shall be submitted in the following form and shall specify shipments made during the preceding year:</li></ol><p>a. A statistical summary report on a form prescribed by the Commissioner, on or before August 1; or</p><p>b. A statistical summary report by electronic transfer, utilizing the Uniform Fertilizer Tonnage Reporting System. Prior to using the electronic transfer method, the person responsible for submitting the annual tonnage report shall make arrangements with the Commissioner for the Commissioner&#39;s receipt of the report by such method.</p><ol start="3"><li>If the annual statistical report is not filed within 15 working days of the specified filing date, a late fee of $35 shall be assessed against the licensee.</li></ol><p>B. For all other regulated products:</p><ol><li>The person distributing or selling such products to a nonlicensee shall furnish the Commissioner an annual report showing:</li></ol><p>a. The county or city of the nonlicensee consignee; and</p><p>b. The amounts (expressed in tons, or decimal portions) of each grade under a given brand of product.</p><ol start="2"><li>Any person listed in subdivision B 1 who fails to file this report by August 1 shall pay a late fee of $35 to the Commissioner.</li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>, § 3.1-106.9; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0104" target="_blank" rel="noreferrer">104</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0564" target="_blank" rel="noreferrer">564</a>.</p>',16),n=[a];function s(l,c,p,h,f,g){return t(),i("div",null,n)}const u=e(o,[["render",s]]);export{m as __pageData,u as default};
