import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-3801 Powers and duties of the Commissioner.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3801.md","filePath":"vacode/3.2-3801.md"}'),a={name:"vacode/3.2-3801.md"},s=r('<h1 id="§-3-2-3801-powers-and-duties-of-the-commissioner" tabindex="-1">§ 3.2-3801 Powers and duties of the Commissioner. <a class="header-anchor" href="#§-3-2-3801-powers-and-duties-of-the-commissioner" aria-label="Permalink to &quot;§ 3.2-3801 Powers and duties of the Commissioner.&quot;">​</a></h1><p>A. The Commissioner shall:</p><ol><li><p>Protect the agricultural, horticultural, and other interests of the Commonwealth from plant pests; and</p></li><li><p>Promptly credit all moneys collected by him as repayment to the fund in the state treasury to which such moneys are owed.</p></li></ol><p>B. The Commissioner may enter into reciprocal agreements with officers of other states so that nursery stock may be sold or delivered in the Commonwealth by out-of-state nurserymen or dealers without the payment of a Virginia registration fee, provided that like privileges are granted to Virginia nurserymen or dealers by such other states.</p><p>Code 1950, §§ 3-178.4, 3-178.15, 3-178.20; 1964, c. 476; 1966, c. 702, §§ 3.1-138, 3.1-149, 3.1-154; 1980, c. 291, §§ 3.1-188.34, 3.1-188.43, 3.1-188.48; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),i=[s];function n(l,c,d,h,m,p){return t(),o("div",null,i)}const u=e(a,[["render",n]]);export{f as __pageData,u as default};
