import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-3904 Powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3904.md","filePath":"vacode/3.2-3904.md"}'),r={name:"vacode/3.2-3904.md"},o=i('<h1 id="§-3-2-3904-powers-and-duties-of-the-board" tabindex="-1">§ 3.2-3904 Powers and duties of the Board. <a class="header-anchor" href="#§-3-2-3904-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 3.2-3904 Powers and duties of the Board.&quot;">​</a></h1><p>The Board shall have the following powers and duties:</p><ol><li><p>Appoint advisory committees as necessary to implement this chapter;</p></li><li><p>Contract for research projects and establish priorities;</p></li><li><p>Consult with the Department of Environmental Quality regarding compliance with the applicable waste management regulations for the safe and proper disposal of pesticide concentrates, used pesticide containers, and unused pesticides;</p></li><li><p>Consult with the Virginia Department of Labor and Industry regarding compliance with the applicable standards and regulations needed to ensure safe working conditions for pest control and agricultural workers;</p></li><li><p>Consult with the Department of Wildlife Resources regarding standards for the protection of wildlife and fish and to further promote cooperation with respect to programs established by the Department of Wildlife Resources for the protection of endangered or threatened species;</p></li><li><p>Inform the citizens of the desirability and availability of nonchemical and less toxic alternatives to chemical pesticides and the benefits of the safe and proper use of pest control products while promoting the use of integrated pest management techniques and encouraging the development of nonchemical and less toxic alternatives to chemical pesticides;</p></li><li><p>Require that pesticides are adequately tested and are safe for use under local conditions;</p></li><li><p>Require that individuals who sell, store, or apply pesticides commercially are adequately trained and observe appropriate safety practices;</p></li><li><p>Cooperate, receive grants-in-aid, and enter into agreements with any federal, state, or local agency to promote the purposes of this chapter;</p></li><li><p>Consult with the Department of Health regarding compliance with public health standards;</p></li><li><p>Designate any pesticide as state special use or classified for restricted use; and</p></li><li><p>Restrict the distribution, possession, sale, or use of tributyltin compounds.</p></li></ol><p>1987, c. 15, § 3.1-249.25; 1989, c. 575, §§ 3.1-249.29, 3.1-249.62; 1991, c. 333; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0633" target="_blank" rel="noreferrer">633</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',4),s=[o];function n(l,p,d,c,h,f){return t(),a("div",null,s)}const m=e(r,[["render",n]]);export{g as __pageData,m as default};
