import{_ as n,o as s,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 3.2-3912 Pesticide Control Fund established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3912.md","filePath":"vacode/3.2-3912.md"}'),a={name:"vacode/3.2-3912.md"},o=e("h1",{id:"§-3-2-3912-pesticide-control-fund-established",tabindex:"-1"},[t("§ 3.2-3912 Pesticide Control Fund established. "),e("a",{class:"header-anchor",href:"#§-3-2-3912-pesticide-control-fund-established","aria-label":'Permalink to "§ 3.2-3912 Pesticide Control Fund established."'},"​")],-1),i=e("p",null,'There is hereby created in the state treasury a special nonreverting fund to be known as the Pesticide Control Fund, hereafter referred to as "the Fund." The Fund shall be established on the books of the Comptroller. All moneys levied and collected under the provisions of this chapter shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used by the Department solely for carrying out the purposes of this chapter. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Commissioner.',-1),d=e("p",null,[t("1989, c. 575, § 3.1-249.34; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),t(".")],-1),l=[o,i,d];function h(c,u,p,f,b,m){return s(),r("div",null,l)}const F=n(a,[["render",h]]);export{y as __pageData,F as default};
