import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 3.2-3944 Stop-sale or removal orders; \\"stop-use\\" orders; judicial review.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-3944.md","filePath":"vacode/3.2-3944.md"}'),r={name:"vacode/3.2-3944.md"},s=i('<h1 id="§-3-2-3944-stop-sale-or-removal-orders-stop-use-orders-judicial-review" tabindex="-1">§ 3.2-3944 Stop-sale or removal orders; &quot;stop-use&quot; orders; judicial review. <a class="header-anchor" href="#§-3-2-3944-stop-sale-or-removal-orders-stop-use-orders-judicial-review" aria-label="Permalink to &quot;§ 3.2-3944 Stop-sale or removal orders; &quot;stop-use&quot; orders; judicial review.&quot;">​</a></h1><p>A. When the Commissioner has reason to believe that a pesticide is being offered for sale or use or is being used in violation of any of the provisions of this chapter, he shall issue and enforce a written or printed &quot;stop sale or removal&quot; order. The order shall be directed to the owner or custodian of the lot of pesticide and shall require him to hold the pesticide at a designated place until this chapter has been complied with and the pesticide is released in writing by the Commissioner or the violation is otherwise legally disposed of by written authority. The owner or custodian of such pesticide shall have the right to administrative and judicial review of such order in accordance with the provisions of the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.). The provisions of this section shall not be construed as limiting the right of the enforcement officer to proceed as authorized by other provisions of this chapter. The Commissioner shall release the pesticide when the requirements of the provisions of this chapter have been complied with and upon payment of all costs and expenses incurred in connection with the withdrawal.</p><p>B. When the Commissioner has reason to believe that any pesticide is being offered for sale or use or is being used in violation of any of the provisions of this chapter by a person, he shall issue and enforce a written or printed &quot;stop-use&quot; order until the Pesticide Control Act has been complied with or the violation has been otherwise legally disposed of by written authority. The person shall have the right to administrative and judicial review of such order in accordance with the provisions of the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.). The provisions of this section shall not be construed as limiting the right of the enforcement officer to proceed as authorized by other provisions of this chapter.</p><p>Code 1950, § 3-208.46; 1966, c. 702, § 3.1-248; 1981, c. 260; 1986, c. 615; 1989, c. 575, § 3.1-249.77; 1993, c. 773; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),a=[s];function n(h,d,l,c,p,f){return o(),t("div",null,a)}const m=e(r,[["render",n]]);export{v as __pageData,m as default};
