import{_ as n,o as a,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 3.2-4004 Seed Fund; established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4004.md","filePath":"vacode/3.2-4004.md"}'),s={name:"vacode/3.2-4004.md"},i=e("h1",{id:"§-3-2-4004-seed-fund-established",tabindex:"-1"},[t("§ 3.2-4004 Seed Fund; established. "),e("a",{class:"header-anchor",href:"#§-3-2-4004-seed-fund-established","aria-label":'Permalink to "§ 3.2-4004 Seed Fund; established."'},"​")],-1),o=e("p",null,'There is hereby created in the state treasury a special nonreverting fund to be known as the Seed Fund, hereafter referred to as the "Fund." The Fund shall be established on the books of the Comptroller. All fees and assessments paid pursuant to this article shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used solely for carrying out the purpose of this article, except that the Commissioner shall deposit 50 percent of the inspection fee to the credit of the Virginia Agricultural Foundation Fund. The Virginia Agricultural Council shall administer all funds received from this section for the exclusive funding of lawn and turf research.',-1),d=e("p",null,[t("1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0577",target:"_blank",rel:"noreferrer"},"577"),t(", § 3.1-275.7; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),t(".")],-1),l=[i,o,d];function h(c,u,f,p,_,g){return a(),r("div",null,l)}const F=n(s,[["render",h]]);export{b as __pageData,F as default};
