import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-4026 Illegal use of word \\"certified\\"; who may make certification; standards; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4026.md","filePath":"vacode/3.2-4026.md"}'),i={name:"vacode/3.2-4026.md"},r=o('<h1 id="§-3-2-4026-illegal-use-of-word-certified-who-may-make-certification-standards-penalty" tabindex="-1">§ 3.2-4026 Illegal use of word &quot;certified&quot;; who may make certification; standards; penalty. <a class="header-anchor" href="#§-3-2-4026-illegal-use-of-word-certified-who-may-make-certification-standards-penalty" aria-label="Permalink to &quot;§ 3.2-4026 Illegal use of word &quot;certified&quot;; who may make certification; standards; penalty.&quot;">​</a></h1><p>A. It is unlawful for any person to use the term &quot;certified&quot; or imply certification relative to any agricultural seeds, vegetable seeds, tubers for planting purposes, or plants offered for sale unless such seeds, tubers, or plants have been certified as follows:</p><ol><li><p>If the seeds, tubers, or plants were produced in another state or in a foreign country, certification by authorized inspection officials of the place of origin shall be sufficient if accepted by the Board.</p></li><li><p>If the seeds, tubers, or plants were produced in Virginia, certification shall be by the producers under authorization of the Board, its agents, or the Department.</p></li></ol><p>B. Any person who violates this section is guilty of a Class 3 misdemeanor. Each violation shall be a separate offense.</p><p>Code 1950, § 3-227; 1966, c. 702, § 3.1-283; 1980, c. 413; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),s=[r];function n(l,c,d,f,p,u){return t(),a("div",null,s)}const m=e(i,[["render",n]]);export{_ as __pageData,m as default};
