import{_ as a,o,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-407 Liens.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-407.md","filePath":"vacode/3.2-407.md"}'),i={name:"vacode/3.2-407.md"},r=e("h1",{id:"§-3-2-407-liens",tabindex:"-1"},[t("§ 3.2-407 Liens. "),e("a",{class:"header-anchor",href:"#§-3-2-407-liens","aria-label":'Permalink to "§ 3.2-407 Liens."'},"​")],-1),s=e("p",null,"If a person who is required to pay a civil penalty under this chapter fails to do so, the Commissioner may transmit a true copy of the order assessing such penalty to the clerk of the circuit court of any county or city wherein it is ascertained that the person owing such penalty has any estate; and the clerk to whom such copy is transmitted shall record it, as a judgment is required by law to be recorded, and shall index it in the name of the Commonwealth as well as in the name of the person owing the civil penalty, and thereupon there shall be a lien in favor of the Commonwealth on the property within such locality of the person owing the civil penalty in the amount of the civil penalty. The Commissioner and Board may collect civil penalties that are owed in the same manner as provided by law in respect to judgment of a circuit court.",-1),l=e("p",null,[t("1996, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0773",target:"_blank",rel:"noreferrer"},"773"),t(", § 10.1-559.8; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),t(".")],-1),c=[r,s,l];function h(d,p,m,f,u,_){return o(),n("div",null,c)}const v=a(i,[["render",h]]);export{g as __pageData,v as default};
