import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-409 Ordinances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-409.md","filePath":"vacode/3.2-409.md"}'),t={name:"vacode/3.2-409.md"},i=n('<h1 id="§-3-2-409-ordinances" tabindex="-1">§ 3.2-409 Ordinances. <a class="header-anchor" href="#§-3-2-409-ordinances" aria-label="Permalink to &quot;§ 3.2-409 Ordinances.&quot;">​</a></h1><p>A. Any locality may adopt an ordinance creating a complaint, investigation, and agricultural stewardship plan development program. Ordinances adopted hereunder may contain only provisions that parallel §§ <a href="http://law.lis.virginia.gov/vacode/3.2-401/" target="_blank" rel="noreferrer">3.2-401</a> and <a href="http://law.lis.virginia.gov/vacode/3.2-402/" target="_blank" rel="noreferrer">3.2-402</a>. No such ordinance shall provide for the imposition of civil or criminal sanctions against an operator or owner who fails to implement a plan. If an owner or operator fails to implement a plan, the local governing body shall submit a complaint to the Commissioner as provided in § <a href="http://law.lis.virginia.gov/vacode/3.2-402/" target="_blank" rel="noreferrer">3.2-402</a>.</p><p>B. This section shall not apply to any ordinance (i) in existence on July 1, 1996 or (ii) adopted pursuant to the Chesapeake Bay Preservation Act (§ <a href="http://law.lis.virginia.gov/vacode/62.1-44.15:67/" target="_blank" rel="noreferrer">62.1-44.15:67</a> et seq.).</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, § 10.1-559.10; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>.</p>',4),o=[i];function l(s,c,p,d,h,g){return a(),r("div",null,o)}const v=e(t,[["render",l]]);export{f as __pageData,v as default};
