import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-4113 Production of industrial hemp lawful.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4113.md","filePath":"vacode/3.2-4113.md"}'),t={name:"vacode/3.2-4113.md"},i=o('<h1 id="§-3-2-4113-production-of-industrial-hemp-lawful" tabindex="-1">§ 3.2-4113 Production of industrial hemp lawful. <a class="header-anchor" href="#§-3-2-4113-production-of-industrial-hemp-lawful" aria-label="Permalink to &quot;§ 3.2-4113 Production of industrial hemp lawful.&quot;">​</a></h1><p>A. It is lawful for a grower, his agent, or a federally licensed hemp producer to grow, a dealer or his agent to deal in, or a processor or his agent to process industrial hemp in the Commonwealth for any lawful purpose. No federally licensed hemp producer or grower or his agent shall be prosecuted under Chapter 11 (§ <a href="/vacode/4.1-1100/">4.1-1100</a> et seq.) of Title 4.1 or § <a href="/vacode/18.2-247/">18.2-247</a>, <a href="/vacode/18.2-248/">18.2-248</a>, <a href="/vacode/18.2-248.01/">18.2-248.01</a>, <a href="/vacode/18.2-248.1/">18.2-248.1</a>, or <a href="/vacode/18.2-250/">18.2-250</a> for the possession or growing of industrial hemp or any Cannabis sativa with a tetrahydrocannabinol concentration that does not exceed the total delta-9 tetrahydrocannabinol concentration percentage established in federal regulations applicable to negligent violations located at 7 C.F.R. 990.6(b)(3). No dealer or his agent or processor or his agent shall be prosecuted under Chapter 11 (§ <a href="/vacode/4.1-1100/">4.1-1100</a> et seq.) of Title 4.1 or § <a href="/vacode/18.2-247/">18.2-247</a>, <a href="/vacode/18.2-248/">18.2-248</a>, <a href="/vacode/18.2-248.01/">18.2-248.01</a>, <a href="/vacode/18.2-248.1/">18.2-248.1</a>, or <a href="/vacode/18.2-250/">18.2-250</a> or issued a summons or judgment for the possession, dealing, or processing of industrial hemp. In any complaint, information, or indictment, and in any action or proceeding brought for the enforcement of any provision of Article 1 (§ <a href="/vacode/18.2-247/">18.2-247</a> et seq.) of Chapter 7 of Title 18.2 or the Drug Control Act (§ <a href="/vacode/54.1-3400/">54.1-3400</a> et seq.), it shall not be necessary to negate any exception, excuse, proviso, or exemption contained in this chapter or the Drug Control Act, and the burden of proof of any such exception, excuse, proviso, or exemption shall be on the defendant.</p><p>B. Nothing in this chapter shall be construed to authorize any person to violate any federal law or regulation.</p><p>C. No person shall be prosecuted under Chapter 11 (§ <a href="/vacode/4.1-1100/">4.1-1100</a> et seq.) of Title 4.1 or § <a href="/vacode/18.2-247/">18.2-247</a>, <a href="/vacode/18.2-248/">18.2-248</a>, <a href="/vacode/18.2-248.01/">18.2-248.01</a>, <a href="/vacode/18.2-248.1/">18.2-248.1</a>, or <a href="/vacode/18.2-250/">18.2-250</a> for the involuntary growth of industrial hemp through the inadvertent natural spread of seeds or pollen as a result of proximity to a production field, dealership, or process site.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0158" target="_blank" rel="noreferrer">158</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0180" target="_blank" rel="noreferrer">180</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0061" target="_blank" rel="noreferrer">61</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0170" target="_blank" rel="noreferrer">170</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0689" target="_blank" rel="noreferrer">689</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0690" target="_blank" rel="noreferrer">690</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0653" target="_blank" rel="noreferrer">653</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0654" target="_blank" rel="noreferrer">654</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0110" target="_blank" rel="noreferrer">110</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',5),n=[i];function l(s,c,h,f,d,p){return r(),a("div",null,n)}const v=e(t,[["render",l]]);export{u as __pageData,v as default};
