import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 3.2-4203 Withdrawal of escrow funds assigned and contributed to the Commonwealth.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4203.md","filePath":"vacode/3.2-4203.md"}'),o={name:"vacode/3.2-4203.md"},n=r('<h1 id="§-3-2-4203-withdrawal-of-escrow-funds-assigned-and-contributed-to-the-commonwealth" tabindex="-1">§ 3.2-4203 Withdrawal of escrow funds assigned and contributed to the Commonwealth. <a class="header-anchor" href="#§-3-2-4203-withdrawal-of-escrow-funds-assigned-and-contributed-to-the-commonwealth" aria-label="Permalink to &quot;§ 3.2-4203 Withdrawal of escrow funds assigned and contributed to the Commonwealth.&quot;">​</a></h1><p>Notwithstanding the provisions of subsection B of § <a href="http://law.lis.virginia.gov/vacode/3.2-4201/" target="_blank" rel="noreferrer">3.2-4201</a>, any escrow funds assigned and contributed to the Commonwealth pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-4202/" target="_blank" rel="noreferrer">3.2-4202</a> shall be withdrawn by the Commonwealth by request of the State Treasurer to the Attorney General and upon approval of the Attorney General. The State Treasurer shall make such request as soon as practicable and such escrow funds withdrawn shall be deposited into the Virginia Health Care Fund established under § <a href="http://law.lis.virginia.gov/vacode/32.1-366/" target="_blank" rel="noreferrer">32.1-366</a>.</p><p>After such withdrawal, any remaining escrow funds shall be withdrawn under the withdrawal procedures provided in this section, and the withdrawn escrow funds shall be deposited into the Virginia Health Care Fund. Nothing in this article shall be construed to relieve a tobacco product manufacturer from any past, current, or future obligations it may have pursuant to Article 1 (§ <a href="http://law.lis.virginia.gov/vacode/3.2-4200/" target="_blank" rel="noreferrer">3.2-4200</a> et seq.) or 3 (§ <a href="http://law.lis.virginia.gov/vacode/3.2-4204/" target="_blank" rel="noreferrer">3.2-4204</a> et seq.).</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0899" target="_blank" rel="noreferrer">899</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0901" target="_blank" rel="noreferrer">901</a>, § 3.1-336.2:2; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>.</p>',4),i=[n];function s(l,d,h,c,f,u){return a(),t("div",null,i)}const w=e(o,[["render",s]]);export{p as __pageData,w as default};
