import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-4204 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4204.md","filePath":"vacode/3.2-4204.md"}'),r={name:"vacode/3.2-4204.md"},o=i('<h1 id="§-3-2-4204-definitions" tabindex="-1">§ 3.2-4204 Definitions. <a class="header-anchor" href="#§-3-2-4204-definitions" aria-label="Permalink to &quot;§ 3.2-4204 Definitions.&quot;">​</a></h1><p>As used in this article:</p><p>&quot;Brand family&quot; means all styles of cigarettes sold under the same trademark and differentiated from one another by means of additional modifiers or descriptors, including, but not limited to, &quot;menthol,&quot; &quot;lights,&quot; &quot;kings,&quot; and &quot;100s&quot; and includes any brand name alone or in conjunction with any other word, trademark, logo, symbol, motto, selling message, recognizable pattern of colors, or any other indicia of product identification identical or similar to, or identifiable with, a previously known brand of cigarettes.</p><p>&quot;Commissioner&quot; means the Tax Commissioner of the Department of Taxation.</p><p>&quot;Nonparticipating manufacturer&quot; means any tobacco product manufacturer that is not a participating manufacturer.</p><p>&quot;Participating manufacturer&quot; shall have the meaning provided in section II (jj) of the Master Settlement Agreement.</p><p>&quot;Stamping agent&quot; means (i) a person who is authorized by the Tax Commissioner pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-1011/" target="_blank" rel="noreferrer">58.1-1011</a> to affix Virginia tax stamps to packages, packs, cartons, or other containers of cigarettes; or (ii) any person who is required to pay the excise tax imposed on cigarettes pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-1001/" target="_blank" rel="noreferrer">58.1-1001</a>.</p><p>Terms defined in § <a href="http://law.lis.virginia.gov/vacode/3.2-4200/" target="_blank" rel="noreferrer">3.2-4200</a> shall have the same meaning when used in this article.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0798" target="_blank" rel="noreferrer">798</a>, § 3.1-336.3; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1029" target="_blank" rel="noreferrer">1029</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',9),n=[o];function s(l,c,p,d,f,u){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
