import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 3.2-4206.01 List of persons ineligible to be authorized holders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4206.01.md","filePath":"vacode/3.2-4206.01.md"}'),r={name:"vacode/3.2-4206.01.md"},n=i('<h1 id="§-3-2-4206-01-list-of-persons-ineligible-to-be-authorized-holders" tabindex="-1">§ 3.2-4206.01 List of persons ineligible to be authorized holders. <a class="header-anchor" href="#§-3-2-4206-01-list-of-persons-ineligible-to-be-authorized-holders" aria-label="Permalink to &quot;§ 3.2-4206.01 List of persons ineligible to be authorized holders.&quot;">​</a></h1><p>A. The Attorney General shall develop and publish on its website a list of individuals who are ineligible to be authorized holders as defined in § <a href="http://law.lis.virginia.gov/vacode/58.1-1000/" target="_blank" rel="noreferrer">58.1-1000</a>. The Attorney General shall update the list as necessary to add names of individuals who are no longer eligible to be authorized holders. Upon request, the Office of the Executive Secretary of the Supreme Court shall provide the Attorney General with assistance to ensure that the requirements of this section are met.</p><p>B. Any attorney for the Commonwealth, law-enforcement officer, or other person may submit a request to the Attorney General that a person be included on the list and shall submit a certified court order of the conviction that makes the person ineligible to be an authorized holder of cigarettes.</p><p>C. Nothing in this section shall impose an affirmative duty on the Attorney General to identify persons to be included on the list who are ineligible to be authorized holders of cigarettes due to a conviction in another state, in the absence of a request received from an attorney for the Commonwealth, law-enforcement officer, or other person.</p><p>D. No liability shall be imposed upon the Attorney General for any omissions or the incorrect inclusion of any individual on the listing required under subsection A. No liability shall be imposed upon any attorney for the Commonwealth or law-enforcement official who provides information to the Attorney General in accordance with subsection B. This provision shall not be construed to grant immunity for gross negligence or willful misconduct.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>.</p>',6),a=[n];function s(l,h,d,c,f,p){return t(),o("div",null,a)}const m=e(r,[["render",s]]);export{b as __pageData,m as default};
