import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 3.2-4318 Jurisdiction to enjoin unlawful use of Label.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4318.md","filePath":"vacode/3.2-4318.md"}'),n={name:"vacode/3.2-4318.md"},o=a('<h1 id="§-3-2-4318-jurisdiction-to-enjoin-unlawful-use-of-label" tabindex="-1">§ 3.2-4318 Jurisdiction to enjoin unlawful use of Label. <a class="header-anchor" href="#§-3-2-4318-jurisdiction-to-enjoin-unlawful-use-of-label" aria-label="Permalink to &quot;§ 3.2-4318 Jurisdiction to enjoin unlawful use of Label.&quot;">​</a></h1><p>A. Any circuit court in the Commonwealth shall have jurisdiction to enjoin the use of the Virginia Quality Label, a label with the shield of the United States, or any imitation or counterfeit likeness used in violation of this article.</p><p>B. The Commissioner may apply for and an appropriate court may grant a temporary or permanent injunction restraining any person from using the labels described in subsection A.</p><p>Code 1950, §§ 3-279, 3-280; 1966, c. 702, §§ 3.1-358, 3.1-359; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0317" target="_blank" rel="noreferrer">317</a>.</p>',4),r=[o];function l(s,c,u,f,d,_){return i(),t("div",null,r)}const g=e(n,[["render",l]]);export{h as __pageData,g as default};
